import React from "react";
import data from "../data/hero.json";

const Hero = ({isBg}) => {
    const {herov1} = data;
    return(
        <section id="hero" className = {'hero hero_padding overflow-hidden position-relative ${isBg === "yes" ? "bg-one": " "}'}>
            <div className="circle x1"></div>
            <div className="circle x2"></div>
            <div className="circle x3"></div>
            <div className="circle x4"></div>
            <div className="circle x5"></div>
            <div className="container">
                <div className="row align-items-center">
                    <div className="col-lg-6 m-opx-b md-m-30px-b">
                        <div className="hero_content position-relative">
                            <div className="badge-text mb-2 text-uppercase">
                                {herov1.subtitle}
                            </div>
                            <h1 className="display-4 mb-4 text-capitalize">{herov1.title}</h1>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    )
}

export default Hero;
